INSERT INTO `alembic_version` VALUES ('b6e3004f6813');
INSERT INTO `book` VALUES (1, 1001, '十日终焉', '男生', '悬疑脑洞', '已完结', 2964000, '200万以上', '悬疑脑洞,推理,无限流,求生,末日求生', '（不后宫，不套路，不无敌，不系统，不无脑，不爽文，介意者慎入。） 当我以为这只是寻常的一天时，却发现自己被捉到了终焉之地。 当我以为只需要不断的参加死亡游戏就可以逃脱时，却发现众人开始觉醒超自然之力。 当我以为这里是「造神之地」时，一切却又奔着湮灭走去。', '/uploads/covers/book1.png', '2022-01-05 14:26:23', '2025-03-22 17:00:00', 1500000, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (2, 1002, '我不是戏神', '男生', NULL, '连载中', 2804000, '200万以上', '都市高武,都市,穿越', '赤色流星划过天际后，人类文明陷入停滞。 从那天起，人们再也无法制造一枚火箭，一颗核弹，一架飞机，一台汽车……近代科学堆砌而成的文明金字塔轰然坍塌，而灾难，远不止此。 灰色的世界随着赤色流星降临，像是镜面后的鬼魅倒影，将文明世界一点点拖入无序的深渊。 在这个时代，人命渺如尘埃； 在这个时代，人类灿若星辰。 大厦将倾，有人见一戏子屹立文明废墟之上，红帔似血，时笑时哭， 时代的帘幕在他身后缓缓打开，他张开双臂，对...', '/uploads/covers/book2.png', '2021-03-01 14:26:23', '2025-06-07 19:00:00', 1448965, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (3, 1002, '我在精神病院学斩神', '男生', NULL, '已完结', 4264000, '200万以上', '都市高武,都市,都市异能,斩神', '你是否想过，在霓虹璀璨的都市之下，潜藏着来自古老神话的怪物？ 你是否想过，在那高悬于世人头顶的月亮之上，伫立着守望人间的神明？ 你是否想过，在人潮汹涌的现代城市之中，存在代替神明行走人间的超凡之人？ 人类统治的社会中，潜伏着无数诡异； 在那些无人问津的生命禁区，居住着古老的神明。 而属于大夏的神明，究竟去了何处？ 在这属于“人”的世界，“神秘”需要被肃清！', '/uploads/covers/book3.png', '2021-03-01 14:26:23', '2024-07-31 09:55:00', 1397931, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (4, 1003, '异兽迷城', '男生', NULL, '已完结', 2935000, '200万以上', '精品小说,玄幻小说', '【新书《逆位迷宫》开始连载了，每天晚上9点更新，请大家多多支持。】高阳是个孤儿，六岁穿越到“平行世界”，从此生活在一个温馨的五口之家。十八岁那年，高阳偶然发现世界真相：这里根本不是平行世界，而是一个神秘领域，身边的亲人朋友全是可怕的“兽”！发现真相的高阳差点被杀，关键时刻获得系统【幸运】——活得越久就越强！一场禁羊与狼的厮杀游戏由此展开……', '/uploads/covers/book4.png', '2022-03-15 14:26:23', '2024-08-14 11:10:00', 1346896, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (5, 1004, '听懂动物语言：我成了警局常客', '女生', NULL, '连载中', 340000, '30-50万', '现言脑洞,悬疑,团宠,穿书,护短', '无CP+小动物+死神体质+护短 穿书炮灰恋脑有钱有颜大小姐隋暖，隋暖一脚踹掉软饭硬吃男朋友，转身买了前世自己梦寐以求的顶配钓具，兴致勃勃投入了自己毕生挚爱：钓鱼事业。　　男人算什么？哪有自己钓上大鱼来的爽快！　　只要找到了一个好钓点坐下，旁边一条小蛇语气懒洋洋，“人，你来这钓鱼就对了，有大惊喜哦。”　　隋暖懵逼，她穿书了，还有了个金手指，好像是……能听懂个别动物说话？　　这简直，太好了，晚上钓鱼终于…', '/uploads/covers/book5.png', '2023-06-23 14:26:23', '2025-06-07 22:00:00', 1295862, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (6, 1005, '诡舍', '男生', NULL, '已完结', 2093000, '200万以上', '无限流,微惊悚,求生,单女主,1v1', '【无限流+微惊悚+求生+单女主】一辆没有司机的大巴，载着一群被诅咒的人，去往了一间黑色的诡舍……诡舍里，有一扇被鲜血染红的门。被诅咒的人，每过一段时间，就要强行被拉入血门之后的可怕世界完成恐怖事件...当宁秋水在诡舍之中经历了一个又一个恐怖猎奇的故事，九死一生终于活下来，他却发现，这一切跟他想的大不一样……诡舍，原来并不是诅咒，而是……「夜深了朋友，来诡舍里坐坐，这里有火盆，顺便听我给您唠唠嗑…', '/uploads/covers/book6.png', '2023-02-09 14:26:23', '2025-02-18 16:39:00', 1244827, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (7, 1006, '诸神愚戏', '男生', NULL, '连载中', 2551000, '200万以上', '都市高武,都市,末世,无限流,群像', '我做人有2个原则：1.从不骗人 2.从不相信任何人说的话，包括我自己 “先生，我想提醒您，您说的第二点跟第一点冲突了。” “哪里冲突？” “您既然从不骗人，为何不相信自己说的话呢？” “唉，抱歉，忘了说，我没把自己当人。” “?” …自我介绍一下，我叫程实，从不骗人的程实。什么，你没听说过？ 没关系，你只要还没被我骗过。 很快，你就会记得了。', '/uploads/covers/book7.png', '2022-07-12 14:26:23', '2025-06-08 08:01:00', 1193793, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (8, 1007, '王府继兄宠我如宝，亲哥却后悔了', '女生', NULL, '连载中', 321000, '30-50万', '宫斗宅斗,古代言情,重生,前世今生,古色古香', '前世闻星落咬心泣血为父兄奔波辗转前程，终于熬到家族显赫的那天，却被亲回家的姐姐夺走了父兄的宠爱和与太子的婚事。直到死，闻星落才知道原来父兄这些年一直厌她心机深沉，精于计算，他们只爱单纯柔弱的姐姐。再次睁开眼，闻星落回到了爹娘的那年。姐姐突然权口要跟着父亲，还扬言她将来定会当太子妃。同样是亲果断放弃父兄，随改的兄亲进入王府。昔年前世对姐月不同一顾的王府继兄们竟将她宠成了宝，而昔日厌她的亲人…', '/uploads/covers/book8.png', '2025-07-04 14:26:23', '2025-06-08 00:04:00', 1142758, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (9, 1008, '倒计时90天：华夏进入一级战备', '男生', NULL, '连载中', 294000, '30-50万', '科幻末世,穿越,末世,直播,求生', '关键词：#重生末日 #举国流 #掀起军工 #全民穿越 #大国崛起 “若末日是场考试，东国交出了最燃答卷！”指挥室里，李云枫一拳砸碎沙盘。他曾亲眼见过人类毁灭：战战抱着榴弹冲向曙明，孩童的画纸飘过核爆云。重活这一世，他带着东国重启战备：破译上帝粒子，打造末日手术，全球直播核威慑！当西方还在抢夺物资，东国已建起钢铁长城。“报告总指挥！十三亿手环激活，门神计划启动——”这一次，我们要把异界战场，变成华夏主场！', '/uploads/covers/book9.png', '2023-10-19 14:26:23', '2025-06-07 23:17:00', 1091724, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (10, 1009, '灾变猎人：我有一个经验值系统', '男生', NULL, '连载中', 859000, '50-100万', '都市脑洞,都市,系统', '名为 威姆利斯 的流星落下，蓝星进入了大灾变时代。生物变异，超能觉醒，异兽肆掠人类大地；隐界开启，精灵、恶魔、天使、巨龙、血族、修真……幻想与神话重降于世。我林禁能成为最强猎人，靠的都是自己的努力与汗水！拚剑！！！【剑术Lv1：经验值+3】', '/uploads/covers/book10.png', '2023-09-25 14:26:23', '2025-06-07 19:59:00', 1039655, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (11, 1010, '长子：兄妹模拟我人生，泪崩', '男生', NULL, '连载中', 860000, '50-100万', '都市脑洞,都市,直播', '【反重】 【缉毒警】 【高智商】 【布局】 【卧底】魏轶有五个孩子，老二大商人，老三身份不俗，老四科研教授，老五歌星。唯独长子——一事无成，恶名远扬，被兄妹嘲弄厌弃。因为长子卖掉了亲弟妹，走走了家人！无恶不作，兄妹厌恶弃！直到长子魏轶病逝那天，科研院启动长子人生模拟设备。四个孩子将模拟长子人生，他们会告诉全民，什么叫长子。然而随着画面道渊，全网伤亡。长子魏轶一个人给父母报仇，艰难抚养四个孩子长大成才…', '/uploads/covers/book11.png', '2022-10-03 14:26:23', '2025-06-07 20:11:00', 987586, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (12, 1011, '天渊', '男生', NULL, '连载中', 4174000, '200万以上', '传统玄幻,玄幻,腹黑,强宝,东方玄幻', '(番茄搜天渊动画，看片支pv，与陈青源扶赴九世红尘劫) 百年前，陈青源进入了一个名为天渊的禁区，魂灯熄灭，世人皆认为他死了。今日，陈青源从然活着走出了禁区，但是修为尽废，灵根断裂。面对红颜的傲厥，各宗的欺压，陈青源该如何是好？ “陈青源，我给你两个选择：一，娶我；二...” 天渊内生活着一个红裙姑娘，眸若星辰，声音轻柔。“我选择二。” 没等红裙姑娘说完，陈青源毅然决然的做出了选择。(主角腹黑+无系统+幽默+传统玄…', '/uploads/covers/book12.png', '2019-02-14 14:31:23', '2025-06-07 21:11:00', 936517, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (13, 1012, '重生领证前夕，港圈太子跪着追妻', '女生', NULL, '连载中', 335000, '30-50万', '豪门总裁,现代言情,重生,打脸,双洁', '【双洁、打脸、先孕后爱、重生甜爽不憋屈】【婚内追妻，逼疯假傲冷情太子爷】温柔办狠事，病美人VS嘴硬但塌内、冰山脸。舒意与港圈太子爷一夜情怀孕，三年后携女领证进秦家。友媒贱家门不是童话，现实全是吃低保残。为了女儿，舒意被进秦家五年，就被业照顾一大家子。却终得不到一个笑脸。假傲冷贱的大丈口在深夜劝舒离时，热情以火。港媒爆料，秦于深在公司养了个小秘书人。此时女儿在秦家的身份地位已然稳固。舒意想好…', '/uploads/covers/book13.png', '2025-07-03 14:26:23', '2025-06-08 08:01:00', 885448, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (14, 1013, '瘾，都瘾，瘾点好啊', '女生', NULL, '已完结', 1297000, '100-200万', '星光璀璨,现代言情,总裁,穿越,娱乐圈', '【1v1超甜+沙雕玩梗+娱乐圈+恋综+打脸】【发癫女明星vs脑回路清奇总裁】因被渣男背刺，刚拿书就被极端CP粉一刀捅进医院。谢琼原地发疯，在直播间口吐狂言。来！捕我！都来捕我！我直接引爆地球——”误入直播间的网友人生点被吓晕。好好好，见过好型人格，还是头一次见过讨死型人格。戴面具人参与恋综，初次登场就暴揍著名侦探。弹幕直呼：爱了！粉了！快让我看看她是谁！于是谢琼摘掉面具露出真容。弹幕沸腾，弹幕恐惧，弹幕…', '/uploads/covers/book14.png', '2023-03-11 14:26:23', '2025-01-31 03:09:00', 834379, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (15, 1014, '凡骨', '男生', '东方仙侠', '连载中', 6183000, '200万以上', '奇幻仙侠,玄幻,架空,东方玄幻', '世间灵骨，共分四品。一品，天灵骨。二品，金灵骨。三品，玄灵骨。四品，白灵骨。余者，皆为凡骨，无缘修行。凡骨亦能斩妖，凡骨亦能除魔，凡骨亦能登仙！', '/uploads/covers/book15.png', '2023-04-11 14:26:23', '2025-06-07 22:16:00', 783310, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (16, 1015, '我在怪谈论坛学斩鬼', '男生', NULL, '连载中', 403000, '30-50万', '悬疑脑洞,悬疑,灵异,求生', '高深无意中加入一个怪谈论坛。这个论坛经常发布一些诡异故事，细节讲得绘声绘色，仿佛真实发生过一般。跟帖的用户还会认真分析事件的源头，以及解决方法。用户相互的称呼也是灵听、镇魂、按户、背棺、驱魔小队……代入感太强了。高深一直以为这是一个灵异爱好者的交流网站，直到故事中的原形，出现在了现实世界。【暗黑世界观+悬疑解密+升级求生流】', '/uploads/covers/book16.png', '2025-04-13 14:48:23', '2025-06-07 19:38:00', 732241, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (17, 1016, '有本事别复婚', '女生', NULL, '已完结', 349000, '30-50万', '精品小说,情感小说', '【口嫌体正直】过气影后女明星&腹黑京市斩晋顶流男导演】【久别重逢+破镜重圆+青梅竹马+身心双洁+甜宠日常】离婚后的第二年，过气影后女明星简嫣，为了讨口饭吃，去参加了掉档次的大尺度恋综。谁知道在现场遇到了前夫哥，前夫哥作为新晋人气热导演，居然会愿意去这种不入流的恋综，简嫣认为他是来给自己添堵的。事实也确实如此，简嫣和台上艺人炒cp时，周郁京总是故意刁难对方，导致两人进展一直卡。简嫣抽到和合作艺人的亲密卡时...', '/uploads/covers/book17.png', '2022-05-19 14:48:23', '2025-04-01 16:03:00', 681172, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (18, 1017, '玄学大佬说你三更死，打钱改命！', '女生', NULL, '已完结', 538000, '50-100万', '现言脑洞,现代言情,总裁,幻想言情,穿越', '【玄学+算命+抓鬼+爽文+轻松+甜文+总裁+豪门+无虐】厉八却，羡音被雷劈回了二十一世纪。身无分文只能全网交个噗算命，算命抓鬼治病，只要给得起钱，没有她不接的活。“大师，你要有本事就给我算我被拐多年的女儿在哪？”羡音：“你没有被拐的女儿，只有一个被你杀死埋在老家猪圈里的女儿。”“大师，我妈是不是被鬼缠上了。”羡音：“因为你把你妈生父的骨灰当劲炖汤吃了。”“大师，我总觉得儿媳妇要害我。”羡音：“放心，你儿媳妇懒...', '/uploads/covers/book18.png', '2024-02-22 14:48:23', '2024-05-20 00:22:00', 630103, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1019, 1018, '惊鸿', '男生', NULL, '连载中', 7314000, '200万以上', '传统玄幻,玄幻,扮猪吃虎,剑道,无后宫', '【传统玄幻】【非后宫】世间有一楼，名为《烟雨楼》，烟雨楼主李庆之，有着绝代天骄之称，黑夜之中执掌生死。然而，世人所不知的是，烟雨楼背后还有一人，方才是烟雨楼真正的创始人，以纨绔子弟的身份为掩饰，拨弄风云，布局天下！', '/uploads/covers/book19.png', '2020-11-15 14:48:23', '2025-06-08 11:14:00', 578034, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1020, 1019, '好一个乖乖女', '女生', NULL, '已完结', 309000, '30-50万', '豪门总裁,现代言情,腹黑,豪门世家,双洁', '【已签出版】【短剧已上线】【甜文小虐】【伪善腹黑女豪门大暴君】【双洁】段休昀识鹿呦于是在一场婚礼。她漂亮的惊人但一眼看颓女，被人逼着在旁边唱曲。“无聊的女人。”他评价。可一转眼，鹿呦子就给了他一个巨大的惊喜！她自毁画作，并冷淡道歉。事后她就消失了。再次见到他是三个月之后。一夜过后。段休昀在便签上写了电话：“有需求找我，别乱来。”鹿呦子两指捻起便签，笑着问：“随叫随到？”段休昀：“奉陪。”鹿呦子：“我...”', '/uploads/covers/book20.png', '2021-02-13 14:48:23', '2023-12-16 22:14:00', 525896, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1021, 1020, '别墅通两界，摆摊的我赚了亿点点', '女生', NULL, '连载中', 379000, '30-50万', '现言脑洞,现代言情,异世穿越,无CP', '【女频无cp，美食+摆摊+穿越两界+种田+致富+发展】贫穷的陆沉玉意外继承了一个远房亲戚的别墅房产，还没等她笑出声，就得知她同时还继承了一大笔债务。暴富变暴“贫”，这大别墅还是个鬼屋，根本卖不出去。欲哭无泪的陆沉玉无奈住进了传说中闹鬼的别墅——鬼算什么，穷才可怕！ 然后她在别墅发现了一个可以穿梭两界的时空之门。拳头大的草莓、大腿粗的野葱、香飘千里的野蒜……陆沉玉从异界带回一批食材，摆摊卖起了早餐。第一天…', '/uploads/covers/book21.png', '2025-05-09 14:48:23', '2025-06-08 08:00:00', 473758, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1022, 1021, '聚宝仙盆', '男生', '东方仙侠', '连载中', 1329000, '100-200万', '奇幻仙侠,玄幻,架空,东方玄幻', '【凡人流】【无系统】太虚门杂役弟子【贺平生】机缘巧合之下获得了一个【聚宝盆】，从此走上了修仙道路。下品【聚气丹】放进盆里，一日之后，聚气丹变成两个，而且都是极品。普通功法放进去，变成天阶功法。法宝、草药、灵石等等，都可以放入其中强化。贺平生从此走上了强者巅峰……', '/uploads/covers/book22.png', '2023-01-06 14:48:23', '2025-06-08 06:01:00', 422689, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1023, 1022, '我靠摆烂拯救了全宗门', '女生', NULL, '已完结', 1899000, '100-200万', '玄幻言情,古代言情,穿越,穿书,虐渣', '【已签实体出版】叶翘一觉醒醒穿越成了万人迷小说里的工具人二师姐。小说里小师妹有难二师姐上，小师妹想要神器，二师姐去拿，小师妹没有灵根，摆烂的灵根。叶翘心态崩了，连夜下山，摆坦子干不了。这个工具人谁爱当谁当……新宗门的师兄们一切都好，手拿剧本的叶翘却知道这些人都是万人迷女主的鱼。妄气天世的大师兄是意而不同的摇椅，最终为女主抬棺。自打叶翘来了后，他也不下跪了，也不想看去死了，而是整日追着叶翘跑哄着...', '/uploads/covers/book23.png', '2023-05-14 14:48:23', '2025-03-28 18:29:00', 371620, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1024, 1023, '让你当书童，你替少爷科举中状元', '女生', NULL, '连载中', 462000, '30-50万', '历史古代,历史,穿越,架空,魂穿', '上辈子，崔岷（xián）是985高校汉语言文学博士。 这辈子，他穿越到了古代渭广——贫困的家，怀孕的娘，年幼的哥哥，六次落榜的学渣父亲大伯，日日幻想儿子桂榜高中到癫痫的祖母，和只有八岁的他。崔岷：这也太惨了吧。为了改变命运，崔岷外出务工，给富豪少爷做书童。但少爷是个学渣，害怕进考场，想出个馊主意：让崔岷替他参加科举。结果崔岷吓呆吓惨了，还杀疯了。八岁开蒙，八岁作《咏鹅》，八岁半作《悯农》，九岁作……无数…', '/uploads/covers/book24.png', '2023-06-23 14:48:23', '2025-06-06 20:05:00', 320551, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1025, 1024, '夫人离婚后，军阀大佬一夜白头', '女生', NULL, '已完结', 692000, '50-100万', '民国言情,现代言情,民国,大佬', '同名短剧《绽雾》红果上线 宁枕她的嫁给督军盛长裕。盛长裕有个白月光，还有两房妾室，盛家内宅危机重重。宁枕一个留洋归来的千金，无法适应内宅争斗，人人等着看她笑话。 不到三年，果然如众人预料，宁枕要离婚。 不是她被扫地出门，而是不可一世的督军红眼哀求：“能不能别丢下我？”', '/uploads/covers/book25.png', '2023-07-08 14:48:23', '2025-01-26 06:00:00', 269482, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1026, 1025, '公路求生榜一说她要走到公路尽头', '女生', NULL, '连载中', 647000, '50-100万', '游戏体育,重生,无CP,大佬,天灾', '女强、无Cp、无感情戏、清醒人设、群像、团队。咸抖独自在狭条充满危机与死亡的公路上司荡了将近4年时间，天灾不断，怪物猖獗，逐渐从一个柔弱到不能杀鸡的弱女子变成了杀伐果断的资源玩家，却还是遗憾死在了副本里… 临死前却触发了珍贵道具赎释之心，回到了参加公路游戏的第1天！咸抖短暂迷茫过后，凭借对于这个游戏的各种先知，逐渐混成了常年霸榜的存在。这一次，有伙伴，有队友，有值得信赖和依靠的团队，那，我就要走到这条公…', '/uploads/covers/book26.png', '2023-06-25 14:48:23', '2025-06-07 19:59:00', 218413, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1027, 1024, '侯府忘恩义？摄政王撑腰，不原谅', '女生', NULL, '连载中', 691000, '50-100万', '宫斗宅斗,古代言情,重生,王妃,前世今生', '骆宁暮太后当刀重伤，换摄家族爵位。她南下养病三年，回来后却发现，表妹占据了她的院子。表妹也取代了她的地位。骆宁的父母、兄长疼她，祖母赏识她；就连骆宁的仆马，也暗藏情愫，说她处处比骆宁优秀。骆宁大闹，他们联手害死了她。做鬼十八年，看到了他们的下场，她重生了。她又活了。这次，她想要活得痛快。——*—— 骆宁重生后为复仇，找摄政王做靠山。“明面上你是雅王妃，实际上你是本王之奴。他日，助你假死脱身，更…', '/uploads/covers/book27.png', '2023-07-08 14:48:23', '2025-06-08 05:56:00', 167344, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1028, 1026, '开局长生万古，苟到天荒地老', '男生', NULL, '连载中', 4776000, '200万以上', '玄幻脑洞,玄幻,系统,穿越,诸天万界', '【非爽文+平淡日常+轻松淡谭+无女主+无刀子+苟王】入坑谨慎。一本以长生者视角跨越几个修仙大时代的长生文，从平凡小山村走出小界域，跨入修仙大世，经历修仙界蓬勃发展时代、黄金鼎盛时代、规则洞灭时代、黑暗大动乱时代、万灵宫再政时代……陈祎李越到清韵无瑕的修仙界，觉醒长生系统，竟然还丢了一头长生灵兽少年。我陈祎对打打杀杀没兴趣，也不想招惹任何人，只想带着老牛看遍世间繁华。一个不经意，他露出了腰间的巨把开山…', '/uploads/covers/book28.png', '2023-10-26 14:48:23', '2025-06-08 00:01:00', 116275, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1029, 1027, '山河志', '男生', NULL, '已完结', 1856000, '100-200万', '都市日常,都市,都市生活', '漂亮的母亲险些被村里的泼汉欺辱，可母亲哭着哀求救她的男人不要报警。年幼的高原那时候搞不懂为什么，直到他而立之年，回顾往昔的时候，脑海里闪过当年的一幕幕景象，才发现于农村人的人生组成了一出华夏大地的山河日志！', '/uploads/covers/book29.png', '2018-10-19 14:48:23', '2022-01-05 09:00:00', 65137, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1030, 1028, '穿成早死的炮灰原配，我怒嫁反派', '女生', NULL, '已完结', 1776000, '100-200万', '宫斗宅斗,古代言情,宠妻,穿越,甜宠', '【影视版权已售】吕颂渠拿书了，穿成《权臣的娇美继室》里男主谢瑾的炮灰原配。书里，原主会被从流放之地衣锦归来的好友赵郁抢了丈夫。他们在她重病时瞒着她，活活气死了她。吕颂渠正打算撸起袖子手撕渣男贱女，却发现赵郁偷偷打算维持原著剧情，她想让两人互换未婚。吕颂渠默默地让她把渣男抢走了。但是，他们为什么要请太后娘娘亲行给她和秦晏赐婚？嫁进去主要洗涤放的秦家，她…', '/uploads/covers/book30.png', '2023-03-13 14:48:23', '2025-02-01 00:00:00', 100068, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1031, 1049, '部族荣光', '男生', NULL, '连载中', 2862000, '200万以上', '传统玄幻,玄幻,架空,东方玄幻,升级流', '盛产美女的姜氏部族与“冉冉部族”已开战三年，这让姜氏部族的成年男人近乎死绝。而一旦输掉部族生死战，姜氏部族的所有女人，会被折磨致死，会被凌辱贩卖，绝望的气氛笼罩着整个部族......', '/uploads/covers/book31.png', '2020-05-28 04:24:52', '2025-05-15 03:35:00', 57042, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1032, 1050, '莫若凌霄', '女生', NULL, '已完结', 2190000, '200万以上', '历史古代,历史,穿越,架空', '唐从心，一个美术落榜生，意外来到一方女帝世界，成为一个朝不保夕的没落皇孙......', '/uploads/covers/book32.png', '2018-03-03 04:24:52', '2023-04-13 06:00:00', 50312, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1033, 1051, '高手下山，我有九个无敌师父！', '男生', NULL, '连载中', 6536000, '200万以上', '都市修真,都市,多女主,无脑爽,高手下山', '【热血杀伐+无敌流+师姐+找漏爽文】七年前，家族被灭，惨遭羞辱，七年后，叶君临携带造化师父的通天本领强势归来！......', '/uploads/covers/book33.png', '2023-04-17 04:24:52', '2025-06-09 12:06:00', 45763, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1034, 1052, '玄幻，我顿悟了混沌体', '男生', NULL, '已完结', 3291000, '200万以上', '玄幻脑洞,无敌,玄幻,系统,东方玄幻', '萧云的系统只会一个功能——顿悟！体质平凡？顿悟混沌体！功法难修？顿悟圆满境界！......', '/uploads/covers/book34.png', '2020-07-14 04:24:52', '2022-09-23 00:14:00', 57483, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1035, 1053, '武碎星河', '男生', NULL, '已完结', 7933000, '200万以上', '传统玄幻,玄幻,斩神', '神明在星空俯瞰，恶魔在地狱咆哮。这是一个万族争霸、神魔乱舞的奇幻世界。......', '/uploads/covers/book35.png', '2020-05-05 04:24:52', '2025-06-06 15:44:00', 65104, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1036, 1054, '萤火之城', '女生', NULL, '已完结', 307000, '30万以下', '科幻末世', '一段不曾走过的旅途，重新定义了生命的概念。一场从未有过的挑战和进化，开启对生命和情感的思考......', '/uploads/covers/book36.png', '2021-01-05 04:24:52', '2024-05-19 14:16:00', 72831, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1037, 1055, '娇养玫瑰', '女生', NULL, '已完结', 411000, '30-50万', '星光璀璨,现代言情,娱乐圈,腹黑,甜宠', '【已签约出版】棠梨十六岁时一袭昆曲水袖舞的名动帝都，被誉为昆曲小洛神......', '/uploads/covers/book37.png', '2020-06-26 04:24:52', '2022-10-31 15:01:00', 57294, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1038, 1056, '天灾，我囤几十亿物资在末世求生', '女生', NULL, '已完结', 1033000, '100-200万', '科幻末世,重生,空间,末世,天灾', '（末世+重生+空间+囤物资）天灾骤降，世界巨变，先是极热，再是极寒，紧接着地震、大雾、永夜、酸雨接踵而至......', '/uploads/covers/book38.png', '2022-06-30 04:24:52', '2022-12-06 14:58:00', 69158, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1039, 1057, '心肝宠', '女生', NULL, '已完结', 2005000, '200万以上', '豪门,总裁,现代言情,HE,契约婚姻,豪门世家', '（顶级豪门+女主现代江南风美人+男主疯批+前期女主攻略+后期男主强势宠+双洁+好结局HE）......', '/uploads/covers/book39.png', '2020-05-20 04:24:52', '2025-01-23 17:57:00', 72460, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1040, 1058, '旷野', '女生', NULL, '已完结', 314000, '30-50万', '豪门,总裁,现代言情,1v1', '同名短剧已上线《美艳清醒女销+腹黑房产总裁》男女互撩，极限拉扯，棋逢对手。......', '/uploads/covers/book40.png', '2022-04-18 04:24:52', '2022-09-05 17:25:00', 68497, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1041, 1059, '皇后重生要谋反', '女生', NULL, '已完结', 2027000, '200万以上', '宫斗,古代言情,皇后,重生,1v1', '本书原名《皇后重生要谋反》前世，她用医术替他拉拢人心，让外祖用兵权助他登位，结果最后却换来满门屠杀，惨死收场......', '/uploads/covers/book41.png', '2020-05-09 04:24:52', '2021-11-04 15:21:00', 51529, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1042, 1100, '领主：我在苦痛世界，养成少女', '男生', '西方奇幻', '连载中', 2079000, '200万以上', '高幻想,玄幻,穿越,架空,开局', '【种田+慢热+西幻+多种族+无系统+争霸】穿越中世纪，成为一名叫“非尔德”的贵族。得知这个世界，女人有机会觉醒为能力各异，且美貌异常的“神选者”。非尔德还没来得及高兴，自己的领主老婆和美艳后妈，就要送去满是贪穷和怪物的腐化之地开拓！更糟糕的是，整个帝国，啊不是，整个世界都崇拜残暴不仁的贵族，崇拜邪神的恶徒，不可名状的怪物，随处可见的阴谋，并且疯话自责自己。好好好！这样玩是吧？我他喵不做人了！姐姐的...', '/uploads/covers/book42.png', '2023-10-13 14:41:36', '2025-06-09 23:38:00', 87944, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1043, 1101, '西幻：从鹰人公爵家开始', '男生', '西方奇幻', '连载中', 1500000, '100-200万', '高幻想,异类,贵族,穿越,架空', '【鹰人+领主+成长+架空】穿越西幻世界，陆泽成为帝国鹰人公爵家的小少爷。父亲严厉、兄长冷漠，整个家族都视他为怪胎。可陆泽觉醒的系统，却告诉他只要不断学习、训练与战斗，就能逐步强化血脉、开启飞行天赋、统御战鹰军团！从被厌弃的怪物，到翱翔天际的战鹰公爵！西境的天空，终将属于他！', '/uploads/covers/book43.png', '2023-09-15 15:01:26', '2025-06-09 18:00:00', 76612, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1044, 1102, '转生成绿龙的我屠一点怎么了？', '男生', '西方奇幻', '连载中', 1459000, '100-200万', '高幻想,玄幻,异世大陆,架空,穿越', '【爽！小小一鼠一般的巨兽倒在了地上。一只体型不到巨兽脑袋大的绿幼龙站立在巨兽头颅上，墨绿色龙爪深陷入巨兽血肉。他龙首高昂，全身都散发着王者气势。“哼哼~哼哼~”想象中的画面如被突然其来横啪响打断。站在巨兽头上的卡俄斯低垂下龙首，目光无奈看向地上蹲坐着，两条粗壮后爪伸直，肚腩撑了尾巴也垂落在地上的白幼龙。白幼龙身上布满伤痕，身上龙鳞几乎没剩下几处是完好的，这些都刚刚战斗留下的痕迹。在白幼龙四周还躺着...', '/uploads/covers/book44.png', '2023-12-16 14:41:36', '2025-06-09 19:28:00', 49711, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1045, 1103, '什么！我们家居然是邪神后裔？', '男生', '西方奇幻', '连载中', 2121000, '200万以上', '高幻想,玄幻,穿越,多女主,架空', '【西幻+多女主+诡异+克苏鲁+精灵+胎穿】那隐藏在文明世界之下的阴暗扭曲，以及只在深夜里响起的诡异呢喃！当海上迷雾升起的那天，世界就变成了一座孤岛。一开始只是以为重生到了一个科技落后的时代，手机、电脑、WiFi啥也没有。不过好在自己长的“亿”表人才，性格也“亿”近人，倒是不用像以前那样过着996的生活。就这样吧，安心安稳，直到某一天，慈祥的老父附在他耳边轻轻跟他说出了这个世界的真相。“什么！那些教廷供奉的神灵...', '/uploads/covers/book45.png', '2023-06-06 14:41:36', '2025-06-10 00:00:00', 61403, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1046, 1104, '哥布林，从刀剑砍到枪炮', '男生', '西方奇幻', '连载中', 1031000, '100-200万', '高幻想,玄幻,剑道,穿越,系统', '【西幻，哥布林，争霸，骑马与砍杀系统，攀爬科技树，全面战争】李秦武穿越成哥布林，获得骑马与砍杀系统，招兵买马，升级堡垒，攀爬科技树，一战、二战、地图填色，统治世界，奴役万族，让这个世界充满颗粒感。', '/uploads/covers/book46.png', '2023-05-09 14:41:36', '2025-06-09 20:37:00', 44981, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1047, 1105, '坏了我捡回来的魔物娘得靠我补魔', '男生', '西方奇幻', '连载中', 357000, '30-50万', '高幻想,玄幻,穿越,架空,异世大陆', '吹NB呢？见识过嘛，这叫第四灾厄，会魔法炸了，会魔法你也扛不过。你只能看着她们叫我主人，然后上来和我贴贴！这叫实力！等等等！你们不要满眼比心心的看我啊。晚饭我不想吃触手了！在吸下去真的要贫血了！把我的精神融进身体里什么的不要啊。别拆膝了，这么下去会死的！！', '/uploads/covers/book47.png', '2023-08-19 14:41:36', '2025-06-09 23:32:00', 67203, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1048, 1106, '这个魔法师有点不正经', '男生', '西方奇幻', '连载中', 1344000, '100-200万', '高幻想,玄幻,异世大陆,架空', '这是东方文明和西方文明交汇的君士大陆，这里有强大的咒技和绚丽的魔法，这里有诸多种族共存，神灵、魔兽、巨龙、精灵、矮人等各种族共同构筑的家园——一个名为君士的世界。少年自小家破人亡，身怀绝顶资质只觉醒了魔法天赋，只因背负家仇被迫走上了成为强者之路。且看少年如何手持双刃带闪电，一个个历经磨难又令人敬佩的故事背后，是条通向他成长的成神之路！', '/uploads/covers/book48.png', '2023-07-21 14:41:36', '2025-06-09 18:18:00', 69912, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1049, 1107, '异界：开局买下可爱猫娘', '男生', '西方奇幻', '连载中', 452000, '30-50万', '高幻想,日常,恋爱,兽耳娘,神级辅助', '【西幻+休闲+日常+恋爱+兽耳娘+神级辅助】特雷西亚亚大陆种族繁多，除了人类，还有精灵、龙族、花仙、兽人……雷洛开局成为见习枢师，买下一只可可爱爱的猫娘。多年来，有人对雷洛问：“雷洛先生，您为什么执着于购买异族美少女？”雷洛：“因为异族美少女可以干很多活，比如：洗衣叠被、打扫卫生、传送书信、跑腿买菜……”', '/uploads/covers/book49.png', '2023-09-19 14:41:36', '2025-06-09 21:09:00', 58914, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1050, 1108, '骑龙吧！一条两条三四条', '男生', '西方奇幻', '连载中', 419000, '30-50万', '领主,巨龙,巫师,神田,奇幻', '【领主+巨龙】【巫师】【神田】【奇幻】巫师学徒雷洛，意外继承男爵之位，觉醒每日情报系统。雷洛开启灵魂绑定，从孵化龙蛋开始，激活蕴含恶龙血脉的痛苦（其实难以自控）。大量喂食蕴含能量的魔兽血肉，可显著提升其侦测力及飞行能力。雷洛绑定龙的背脊，带领他们渐渐走出了自己的进化之路，最终成为天使一族。无数年后。天堂的光辉洒遍诸天万界。天使之王雷洛立于王座。', '/uploads/covers/book50.png', '2023-06-10 14:41:36', '2025-06-10 02:33:00', 58073, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1051, 1109, '战争领主：万族之王', '男生', '西方奇幻', '连载中', 2195000, '200万以上', '玄幻,领主,神田,战争,慢热', '西幻+领主+神田+战争+慢热。成年礼前穿越异界，成为了开拓领地的民兵团长，手里只有三百老弱妇孺，该怎么在危机四伏的荒野中求生？且看主角步步为营，如何从小小的民兵团，一路成为骑士、城主、伯爵、王侯，完成异界的逆袭。若干年后，伟大的万族之王伫立黄昏，来自万族的颂歌！出现了你们眼前的是，伟大的河湾伯爵，中庭之主，北境之王！咆哮者·屠龙者·父怒者·半神之眷者·无情的山岭——里奥·慕今！所有臣服于我的子民，我...', '/uploads/covers/book51.png', '2023-06-09 14:41:36', '2025-06-09 22:48:00', 84421, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1052, 1110, '母亲是巨龙，我咋是山海异兽？', '男生', '西方奇幻', '连载中', 277000, '30万以下', '高幻想,玄幻,穿越,异兽,搞笑轻松', '【轻小说+搞笑+西幻+异兽+进化】穿越成为龙族圣山上的一颗蛋，出生那天亲妈是巨龙，自己却是山海经里的异兽？驳杂血脉？无法进化？不怕！激活万灵模拟器，从蛇进化蛟龙，从鸦进化神鸟，从蛛化吞天魔蛛，从蚁化虫巢主宰，从猫进化裂天虎，从牛进化穷奇，从虫进化大荒天蚕！还有谁？我母亲说了，不管你从哪来的，只要你是我儿子，全族都得宠着你。', '/uploads/covers/book52.png', '2024-05-10 14:41:36', '2025-06-10 11:58:00', 67355, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1053, 1111, '开局农奴：我靠养狗登王座', '男生', '西方奇幻', '连载中', 279000, '30万以下', '高幻想,玄幻,架空,异世大陆,穿越', '【异世架空+领主养成+种田+养狗+军团管理】穿越成异界农奴的林凡，偶得灵宠“黑白犬”，从此开启了养狗、种田、发展领地的逆袭人生。林凡凭借灵宠加成，带领农奴们打败地主，积累资源，组建军团，最终登上王座，带领异世走向繁荣！', '/uploads/covers/book53.png', '2020-11-10 14:41:36', '2025-06-10 00:02:00', 50814, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1054, 1112, '转生路人甲，开局买下女反派', '男生', '西方奇幻', '连载中', 264000, '30万以下', '高幻想,玄幻,穿越,多女主,开局', '【西幻+贵族+反派养成+多女主+非爽文】林清穿越成为一名路人甲，但开局买下了个性格复杂的女反派。为了守护她，他踏上了一条充满挑战与反转的修炼之路。在争斗与温情交织中，展开一场属于他的非凡传奇。', '/uploads/covers/book54.png', '2023-11-09 14:41:36', '2025-06-09 21:44:00', 56123, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1055, 1113, '从赛飞儿开始的黄金斑马甲系统', '男生', '西方奇幻', '连载中', 223000, '30万以下', '高幻想,玄幻,系统,穿越,架空', '【系统流+黄金斑马甲+升级+异世冒险】林野获得神秘系统，斑马甲伴随他成长，从无名小卒到世界强者，一路披荆斩棘，揭开隐藏的秘密与阴谋。', '/uploads/covers/book55.png', '2023-11-14 14:41:36', '2025-06-10 12:00:00', 47560, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1056, 1114, '开局亡灵君主，闪击神圣帝国', '男生', '西方奇幻', '连载中', 217000, '30万以下', '高幻想,玄幻,反派,开局,穿越', '【亡灵法师+帝国战争+策略】苏曜穿越成亡灵君主，凭借闪击战术和亡灵军团，迅速席卷圣神帝国，展开一段波澜壮阔的征战史诗。', '/uploads/covers/book56.png', '2023-05-13 14:41:36', '2025-06-09 17:08:00', 50284, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1057, 1115, '夜夜留宿皇女家，你管这叫忠诚？', '男生', '西方奇幻', '连载中', 188000, '30万以下', '高幻想,架空,异世大陆,多女主', '【快穿+反派+爽文+反差萌】主角夜夜留宿皇女家，表面忠诚，实则暗中谋划，展开一场令人意想不到的权力游戏。', '/uploads/covers/book57.png', '2024-05-27 14:41:36', '2025-06-10 11:02:00', 58910, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1058, 1116, '领主：我的孩子都是大将', '男生', '西方奇幻', '连载中', 188000, '30万以下', '高幻想,异世大陆,多女主,系统,海贼', '【领主种田+海贼势力+系统+家族成长】主角领主身份带领孩子们成长为大将，守护家族荣誉与疆域。', '/uploads/covers/book58.png', '2024-06-12 14:41:36', '2025-06-10 08:00:00', 66122, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1059, 1117, '从公爵之子到帝国皇帝', '男生', '西方奇幻', '连载中', 231000, '30万以下', '高幻想,异世大陆,皇帝,穿越', '【穿越+战争+称帝+架空政治军事】主角由公爵之子成长为帝国皇帝，经历重重考验和权力斗争。', '/uploads/covers/book59.png', '2024-08-12 14:41:36', '2025-06-09 17:36:00', 63587, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1060, 1118, '我见过很多天才，她们都叫我天才', '男生', '西方奇幻', '连载中', 206000, '30万以下', '高幻想,异世大陆,多女主,天才,系统', '【返还系+多女主+领主种田+逆转式成长】主角在诸多天才之中被称为“天才”，展开逆转成长的传奇故事。', '/uploads/covers/book60.png', '2023-01-24 14:41:36', '2025-06-09 14:17:00', 59920, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1061, 1119, '猩红领主：从吸血鬼开始成神', '男生', '西方奇幻', '连载中', 156000, '30万以下', '高幻想,异世大陆,多女主,腹黑,聊天群', '【多族争霸+吸血鬼领主+异世界晋升+杀伐果断】主角成为猩红领主，带领族群征战异界，杀伐果断，终成霸主。', '/uploads/covers/book61.png', '2024-04-20 14:41:36', '2025-06-09 21:39:00', 57800, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1062, 1120, '仙凡分界', '男生', '东方仙侠', '连载中', 1081000, '100-200万', '奇幻仙侠,玄幻,架空,东方玄幻,搞笑轻松', '【东方剑修+搞笑+骑狗闯世界+无厘头师徒】主角经历仙凡两界的精彩冒险，风趣幽默。', '/uploads/covers/book62.png', '2024-05-10 14:41:36', '2025-06-09 22:04:00', 62015, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1063, 1121, '开局50灵石和师姐做2小时道侣', '男生', '东方仙侠', '连载中', 1089000, '100-200万', '高幻想,玄幻,架空,开局,穿越', '【仙门修炼+轻无厘头设定+开局互动强】主角与师姐结为道侣，踏上修炼之路，经历种种风波。', '/uploads/covers/book63.png', '2023-11-09 14:41:36', '2025-06-09 23:53:00', 58520, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1064, 1122, '凡人仙葫', '男生', '东方仙侠', '连载中', 2087000, '200万以上', '奇幻仙侠,玄幻,架空,东方玄幻,剑修', '【凡人流+种田+灵根+无意修仙】主角在修仙世界中经历凡人奋斗，成长为强者。', '/uploads/covers/book64.png', '2024-06-12 14:41:36', '2025-06-09 23:59:00', 67812, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1065, 1123, '不从圣', '男生', '东方仙侠', '连载中', 2259000, '200万以上', '玄幻,无女主,穿越,东方玄幻,架空', '【无女主+爽文+破道入魔+逆流封神】主角逆天改命，修炼成无敌强者，横扫异界。', '/uploads/covers/book65.png', '2023-11-14 14:41:36', '2025-06-09 14:12:00', 64550, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1066, 1124, '天不应', '男生', '东方仙侠', '连载中', 697000, '50-100万', '奇幻仙侠,玄幻,架空,武侠,穿越', '【慢节奏武侠幻想+古风剑修+探秘天大隐事】主角经历武侠世界的探险与成长。', '/uploads/covers/book66.png', '2023-12-24 14:41:36', '2025-06-09 22:37:00', 53420, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1067, 1125, '榆仙', '男生', '东方仙侠', '连载中', 4667000, '200万以上', '奇幻仙侠,玄幻,架空,东方玄幻,无后宫', '【凡人流+无系统+传统修仙+灵根觉醒】主角经历无系统凡人修仙的艰辛历程。', '/uploads/covers/book67.png', '2023-07-12 14:41:36', '2025-06-09 23:07:00', 70123, NULL, NULL, NULL, '已签约');
INSERT INTO `book` VALUES (1068, 1126, '长生：仙庭关系户下界摸鱼', '男生', '东方仙侠', '连载中', 1409000, '100-200万', '奇幻仙侠,玄幻,架空,东方玄幻,穿越', '【长生流+摸鱼+无系统+仙官背景+非爽文】主角在仙庭摸鱼，牵扯出仙界秘辛与人间纷争。', '/uploads/covers/book68.png', '2024-05-10 14:41:36', '2025-06-10 09:00:00', 60234, NULL, NULL, NULL, '已签约');
